<?php

	$time = generatedtime($_SESSION['conv_start'], $_SESSION['conv_end']);
	include './'.$_SESSION['forum'].'/_config.php';

	// Should not be here... redirect to the init page	
	if($time == '0.00')	
		redirect('index.php?page=init', 'Please go to the init page to start the converter.');

	// Status settings
	$status = array();
	if($_SESSION['pun_version'] == '1.1')
	{
		$status['adm'] = 'status=2';
		$status['!adm'] = 'status!=2';
		$status['mod'] = 'status=1';
		$status['!mod'] = 'status!=1';
		$status['usr'] = 'status=0';
	}
	else
	{
		$status['adm'] = 'group_id=1';
		$status['!adm'] = 'group_id!=1';
		$status['mod'] = 'group_id=2';
		$status['!mod'] = 'group_id!=2';
		$status['usr'] = 'group_id=4';
	}

	if(isset($_GET['remove'])){

		if($_GET['remove'] == 'a_all')
			$db->query('UPDATE '.$_SESSION['pun'].'users SET '.$status['usr'].' WHERE '.$status['adm']) or myerror('Unable to remove all admins', __FILE__, __LINE__, $db->query());

		else if($_GET['remove'] == 'm_all')
			$db->query('UPDATE '.$_SESSION['pun'].'users SET '.$status['usr'].' WHERE '.$status['mod']) or myerror('Unable to remove all moderators', __FILE__, __LINE__, $db->query());
	
		else
		{
			$uid = intval($_GET['remove']);
			$db->query('UPDATE '.$_SESSION['pun'].'users SET '.$status['usr'].' WHERE id='.$uid) or myerror('Unable to remove user status', __FILE__, __LINE__, $db->query());
		}

	}

	else if(isset($_POST['newadmin'])){

		$db->query('UPDATE '.$_SESSION['pun'].'users SET '.$status['adm'].' WHERE id='.intval($_POST['newadmin']).' AND id>1') or myerror('Unable to change user status to admin', __FILE__, __LINE__, $db->query());

	}

	else if(isset($_POST['newmoderator'])){

		$db->query('UPDATE '.$_SESSION['pun'].'users SET '.$status['mod'].' WHERE id='.intval($_POST['newmoderator']).' AND id>1') or myerror('Unable to change user status to moderator', __FILE__, __LINE__, $db->query());

	}

